const axios = require('axios')
const cheerio = require('cheerio')

async function Pinterest(search){
    return new Promise(async(resolve,reject) => {
        if (search == undefined) return reject({ error: 'enter search parameters!!'})
        axios.get('https://id.pinterest.com/search/pins/?autologin=true&q=' + search, {
            headers: {
                "cookie" : "_auth=1; _b=\"AVna7S1p7l1C5I9u0+nR3YzijpvXOPc6d09SyCzO+DcwpersQH36SmGiYfymBKhZcGg=\"; _pinterest_; _ir=0"
            }
        }).then(async({ data }) => {
            const x = cheerio.load(data)
            const result = [];
            const _output = [];
            x('div > a').get().map(y => {
                const _url = x(y).find('img').attr('src')
                result.push(_url)
            });
            result.forEach(z => {
                if(z == undefined) return
                _output.push(z.replace(/236/g,'736'))
            })
            _output.shift();
            let image = _output[Math.floor(Math.random() * _output.length)]
            resolve({
                date: new Date(),
                result: {
                    image: image
                },
                note: 'It is strictly forbidden to reupload this code, copyright Â© 2022 by Loli Killers'
            })
        })
        .catch(error => {
            reject({
                code: 404,
                message: "Internal Server Error, Repeat in a few more moments, if there is still an error, please contact the coder"
            })
        })
    })
}
module.exports.Pinterest = Pinterest